import { Component } from '@angular/core';
import { _Card } from 'src/app/classes/cardClass';
import { Card } from 'src/app/interfaces/card';


@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {
CardObject:Card = {
  Enddate:"",
  text: '',
  imageUrl: null
}



submit():void {
  let card = new _Card()
}

chooseImage(event:Event | any):void{

}
}
<form (ngSubmit)="submit()">
    <div class="mb-3">
        <label for="exampleFormControlInput1" class="form-label">End date</label>
        <input [(ngModel)]="CardObject.Enddate" name="dateInput" type="date" class="form-control" id="exampleFormControlInput1">
      </div>
      <div class="mb-3">
        <label for="exampleFormControlTextarea1" class="form-label">Content</label>
        <textarea [(ngModel)]="CardObject.text" name="textInput" class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
      </div>
      <div class="mb-3">
        <input (change)="chooseImage($event)" name="fileInp" type="file" class="form-control" id="exampleFormControlInput2" >
      </div>
      <button type="submit" class="btn btn-primary">Submit</button>
</form>
