import { Component } from "@angular/core";

@Component({
    selector:"app-pearent",
    templateUrl:"./pearent.component.html",
    styleUrls:["./pearent.component.css"]
})

export class PearentComponent{
public name:string = "Tazo";
public lastName!:string
public image:string = "https://upload.wikimedia.org/wikipedia/commons/b/b6/Image_created_with_a_mobile_phone.png"
public lastNameEmitterSubscriber(event:string){
this.lastName = event;
}

}
