  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements OnInit{

  subject:Subject<any> = new Subject();


  usersArray:User[] = [];


public  cardEmitterSubscriber(event:User):void{
