import { Component } from '@angular/core';

enum Postion{
  ArrowUp="ArrowUp",
  ArrowDown="ArrowDown",
  ArrowRight="ArrowRight",
  ArrowLeft="ArrowLeft"
}
@Component({
  selector: 'app-test',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {
postion = Postion;
currentPostion = Postion.ArrowUp

  testMethod(key:any):void{
    this.currentPostion = key
    if(this.currentPostion == this.postion.ArrowDown){
      console.log("down from if");
  }else if (this.currentPostion == this.postion.ArrowLeft){
    console.log("left from if");
  }

  }
}
