import { Card } from './interfaces/card';

 @Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})


export class AppComponent {  
  array:Array<Card> = [
    {
      name: 'Card 1',
      description: 'This is the first card',
      image: 'https://media.istockphoto.com/id/1354697451/vector/modern-vip-bank-card-with-world-map-vector-mockup.jpg?s=612x612&w=0&k=20&c=GyQ0bUNS02CBLTgo_VXWHzeciuCHwJk6l2pv0se6_58=',
    }
  ]
}
