import { Component, Input } from '@angular/core';
import { Card } from '../form/card';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent {
@Input() cardObject:Card = {} as Card;
@Input() testStr!:string

public btn():void{
  console.log(this.cardObject);

}
}
<div class="card" style="width: 18rem;">
    <img [src]="cardObject.image" class="card-img-top" alt="...">
    <div class="card-body">
      <h5 class="card-title">{{cardObject.name}}</h5>
      <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
      <h2>price:{{cardObject.price}}</h2>
      <a href="#" class="btn btn-primary">Go somewhere</a>
      <button (click)="btn()">show</button>
    </div>
  </div>
