import { AfterViewChecked, AfterViewInit, Component, ElementRef, ViewChild } from '@angular/core';
import { Card } from 'src/app/components/form/card';
import { CardComponent } from '../card/card.component';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements AfterViewInit {
public cardsArray:Card[] = [];
@ViewChild(CardComponent, { static: true }) appCard!: CardComponent;
@ViewChild('tazo') element!:ElementRef
constructor() {
}

ngAfterViewInit(): void {
  this.element.nativeElement.style.backgroundColor = "red"

  if (this.appCard) {
    this.appCard.hello();
  } else {
    console.error('CardComponent is not initialized.');
  }
}
public  cardObject:Card = {
    name: '',
    image: '',
    price: 0,
    status:"noactive"
  }

  public submit():void{
this.cardsArray.push(this.cardObject);
this.cardObject = {
    name: '',
    price: 0,
    image: '',
    status:"noactive"
  }
  console.log(this.cardsArray)  
  }

  public uploadFile(event:Event | any){
let file:File = event.target.files[0];

let fileReader:FileReader = new FileReader();

fileReader.readAsDataURL(file);

fileReader.onload = () => {
  this.cardObject.image = fileReader.result?.toString();
}
  }


}
