import { Directive, HostListener } from "@angular/core";



@Directive({
    selector: '[appTest]'
})


export class TestDirective {
    constructor(){}


    @HostListener('mousedown' , ['$event'])
    onClick(e:Event) {
        console.log('test' , e);
    }
}
