import { Component } from '@angular/core';
import { Card } from './class/card';

 @Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})


export class AppComponent {  
  cardsArray:Card[] = [];


  cardObjectEmitterSubscriber(event:Card):void{
this.cardsArray.push(event);
console.log(this.cardsArray);
}

}
<div class="form_container">
  <app-form (cardObjectEmitter)="cardObjectEmitterSubscriber($event)"></app-form>
</div>


<div class="card_container">
  <div class="center_card_container">
    <ng-container *ngIf="cardsArray.length > 0">
      <ng-container *ngFor="let card of cardsArray">
        <app-card [card]="card"></app-card>
      </ng-container>
    </ng-container>
  </div>
</div>
