@Component({
  selector: 'app-test',
  templateUrl: './test.component.html',
  styleUrls: ['./test.component.css']
})
export class TestComponent {
  count: number = 0;

  handleCountChange(count: number) {
    this.count = count;
  }}
<button appTest>Click me</button>

<div>{{ count }}</div>
