import { Component } from '@angular/core';

 @Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {

  task = {
    firstName:"",
    lastName:"",
    j:"კლასიკური"
  }

  array:Array<any> = [];

  submit():void{
    this.array.push(this.task);
    this.task.firstName = "";
    this.task.lastName = "";
  }
}
