import { Component, EventEmitter, Output } from '@angular/core';
import { User } from 'src/app/class/user';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {
@Output() userObjectEmitter:EventEmitter<User> = new EventEmitter();
  public userObject:User = {
  name: '',
  lastName: '',
  status: 'active'
}


public submit():void{
  if(this.userObject.name != '' && this.userObject.lastName != '' && this.userObject.status != ''){
    let user = new User(this.userObject.name, this.userObject.lastName, this.userObject.status);
    this.userObjectEmitter.emit(user);
    this.userObject = {
      name: '',
      lastName: '',
      status: 'active'
    }
  }
}
}
