import { Component, EventEmitter, Output } from '@angular/core';
import { User } from '../card/card';
import { TemplateBindingParseResult } from '@angular/compiler';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {
@Output() cardEmitter:EventEmitter<User> = new EventEmitter();
userObject:User = {
  firstName: '',
  lastName: '',
  status: 'active'
}
public submit(){
let user = new User(this.userObject.firstName, this.userObject.lastName, this.userObject.status)
this.cardEmitter.emit(user)
}
}
