import { Component, EventEmitter, Input, Output } from '@angular/core';
import { _Card } from 'src/app/classes/cardClass';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent {
@Input() card:_Card = {} as _Card;
@Output() deleteCardEmitter:EventEmitter<_Card> = new EventEmitter<_Card>()


deleteCard():void{
this.deleteCardEmitter.emit(this.card);
}
}
