import { Directive, HostBinding, HostListener } from "@angular/core";



@Directive({
    selector: '[appTest]'
})


export class TestDirective {
    constructor(){}

    @HostBinding('style.backgroundColor') bg:string = 'blue'

    @HostListener('click' , ['$event'])
    onClick(e:Event | any) {
        console.log('test' , e.target['innerText']);
    }
}
