import { Component } from '@angular/core';
import { Card } from 'src/app/interfaces/card';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {
CardObject:Card = {
  date: new Date(),
  text: '',
  imageUrl: null
}
}
<form>
    <div class="mb-3">
        <label for="exampleFormControlInput1" class="form-label">date</label>
        <input name="dateInput" type="date" class="form-control" id="exampleFormControlInput1">
      </div>
      <div class="mb-3">
        <label for="exampleFormControlTextarea1" class="form-label">Example textarea</label>
        <textarea name="textInput" class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
      </div>
      <div class="mb-3">
        <input name="fileInp" type="file" class="form-control" id="exampleFormControlInput2" >
      </div>
</form>
