import { Component, Input } from '@angular/core';
import { User } from 'src/app/class/user';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent {
@Input() user:User = {} as User
@Input() index!:number
}
