import { AfterViewInit, Component, ElementRef, OnInit, ViewChild } from "@angular/core";
import { ChildComponent } from "../child/child.component";

@Component({
    selector:"app-pearent",
    templateUrl:"./pearent.component.html",
    styleUrls:["./pearent.component.css"]
})

export class PearentComponent implements OnInit,AfterViewInit{
    @ViewChild("testL") element!:ElementRef
    @ViewChild(ChildComponent) appChild!:ChildComponent

    ngOnInit(): void {
        console.log("hello");
    }

    ngAfterViewInit(): void {
        this.element.nativeElement.style.color= "red";
        this.appChild.send();
    }

public name:string = "Tazo";
public tempBoolean = false;
public lastName!:string
public bgColor:string = 'green';
public image:string = "https://pbs.twimg.com/profile_images/1701878932176351232/AlNU3WTK_400x400.jpg"
public lastNameEmitterSubscriber(event:string){
this.lastName = event;
}

constructor(){

}


addBgImage():string{
    return `url(${this.image})`
}

style = {
    backgroundColor:this.bgColor
}
}
