import { Component, EventEmitter, Output } from '@angular/core';
import { Card } from 'src/app/class/card';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {
@Output() cardObjectEmitter:EventEmitter<Card> = new EventEmitter();
  cardObject:Card = {
    content:"",
    date:"",
    imageUrl:null
  }

  submit():void{
    let card:Card = new Card(this.cardObject.content,this.cardObject.date,this.cardObject.imageUrl);
    this.cardObjectEmitter.emit(card);
    this.cardObject = {
      content:"",
      date:"",
      imageUrl:null
    }
  }


  uploadPicture(event:Event | any):void{
    let file:File = event.target.files[0];
    let reader:FileReader = new FileReader();
    reader.onload = () => {
      this.cardObject.imageUrl = reader.result as string;
    }

    reader.readAsDataURL(file)
  }
}
