import { Component } from '@angular/core';
import { _Card } from './classes/cardClass';

 @Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})

export class AppComponent {  
  cardsArray:Array<_Card> = [];
 constructor(){ }


 CardEmitterSubscriber(event:_Card):void{
this.cardsArray.push(event)
 }

 deleteCardEmitterSubscriber(event:number):void{
this.cardsArray.splice(event, 1);
 }
}
