  styleUrls: ['./app.component.css'],
})


export class AppComponent {  
  array:Array<Card> = [
    {
      name: 'Card 1',
      description: 'This is the first card',
      image:'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSYscfUBUbqwGd_DHVhG-ZjCOD7MUpxp4uhNe7toUg4ug&s',
    }
  ]
}
