import { Component } from "@angular/core";

@Component({
    selector:"app-pearent",
    templateUrl:"./pearent.component.html",
    styleUrls:["./pearent.component.css"]
})

export class PearentComponent{
public name:string = "Tazo";
public tempBoolean = false;
public lastName!:string
public bgColor:string = 'green';
public image:string = "https://pbs.twimg.com/profile_images/1701878932176351232/AlNU3WTK_400x400.jpg"
public lastNameEmitterSubscriber(event:string){
this.lastName = event;
}


addBgImage():string{
    return `url(${this.image})`
}

style = {
    backgroundColor:this.bgColor
}
}
