import { Directive, HostBinding, HostListener } from "@angular/core";


@Directive({
    selector: '[appTest]'
})

export class TestDirective{


    @HostBinding('style.backgroundColor') bg:string = 'red'

    @HostListener('click')
    onClick(){
        this.bg = this.bg == 'red' ? 'green' : 'red'
        alert("clicked")
    }

    // @HostListener('mousemove')
